class Day01 : BaseDay {
    override fun run() {
        println("Day01")

        println(calculateFloor(INPUT))
        println(getBasementPosition(INPUT))

        println()
    }

    fun calculateFloor(input : String) : Int {
        var floorUp = 0
        var floorDown = 0

        for (c in input) {
            when (c) {
                '(' -> floorUp++
                ')' -> floorDown++
            }
        }

        return floorUp - floorDown
    }

    fun getBasementPosition(input : String) : Int {
        var index = 1
        var floor = 0

        for (c in input) {
            when (c) {
                '(' -> floor++
                ')' -> floor--
            }

            if (floor == -1) return index

            index++
        }

        return 0
    }

